graphql.query:
query {
  objects(classification: "Prints", size: 10, page: 2, sort: "rank", sortorder: "desc", hasimage: 1, verificationlevel: 4) {
    records {
      id
      title
      images {
        width
        height
      }
    }
    info {
      prev
      next
      totalrecords
    }
  }
}
